            staker.amount4BountyAndVote = uint256(uint248(staker.amount4BountyAndVote)).add(amount) |
                                        (_vote<<VOTE_BIT_INDEX);
        } else {
            staker.amount4BountyAndVote |= (_vote<<VOTE_BIT_INDEX);
